//componenet is just a js function
//components are just custom html elements
//alt shift f to format -- prettier
import "./ExpenseItem.css";

function Expenseitem(props){
//  var date1 = new Date(2022,1,13);
//  var expenseTitle = 'Car Insurance';
//   var expenseAmount = 294.67;
  var month = props.date.toLocaleString('en-US', {month : 'long'})
  return (
    <div className="expense-item">
      <div>
        <div>{month}</div>
        <div></div>
        <div></div>
      </div>
      <div className="expense-item__description">
        <h2>{props.title}</h2>
        <div className="expense-item__price"><p>${props.amount}</p></div>
      </div>
    </div>
  );
}

export default Expenseitem;
