import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import NewTask from "./comps/NewTask";
import ShowTask from "./comps/ShowTask";
import { useState, useEffect } from "react";
import { db } from "./firebase";
import { uid } from "uid";
import { onValue, ref, remove, set, update } from "firebase/database";

function App() {
  const testData = [];

  const [tasks, setTasks] = useState(testData);

  ////////////////
  const [isLoading, setIsLoading] = useState(false);

  const fetchTasks = (taskText) => {
    setIsLoading(true);
    onValue(ref(db), (snapshot) => {
      setTasks([]);
      const data = snapshot.val();
      if (data !== null) {
        Object.values(data).map((taskText) => {
          setTasks((oldArray) => [...oldArray, taskText]);
        });
      }
      setIsLoading(false);

    });

  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const saveTextFunc = (taskText) => {
    const uuid = uid();
    set(ref(db, `/${uuid}`), { ...taskText });
    // fetchTasks();
    // const data = await response.json();
  };

  ///Delelte
  const deleteTextFunc = (taskText) => {
    remove(ref(db, `/${taskText.id}`));
    // db().ref(`/${taskText.id}`).remove();
    // fetchTasks();
    console.log(taskText);

  };

  ///update
  const updateFunc = (taskData) => {
    console.log(taskData.id);
    // update(ref(db, `/${taskData.id}`),
    // {
    //   taskStatus: true,
    //   // todo: 'Hello WOrld'
    //   // uuid: taskData.id
    // })
    console.log(taskData);
  };

  return (
    <>
      <NewTask onSaveText={saveTextFunc}></NewTask>

      <ShowTask
        items={tasks}
        isLoadingProp={isLoading}
        onDeleteText={deleteTextFunc}
        onDone={updateFunc}
      ></ShowTask>
    </>
  );
}

export default App;
